import { useState } from "react"

export default function FetchRandomBeer({random}) {
    const [randomBeer, setRandomBeer] = useState([])
  return (
    fetch("https://api.punkapi.com/v2/beers/random")
      .then((r) => r.json())
      .then((data) => setRandomBeer(data))
  )
}
